import {
	LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE,
	LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE,
	SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE,
	CHANGE_NICKNAME_REQUEST, CHANGE_NICKNAME_SUCCESS, CHANGE_NICKNAME_FAILURE,
	FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE,
	UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS, UNFOLLOW_FAILURE, ADD_POST_TO_ME, REMOVE_POST_OF_ME,
} from "../actions"; // prettier-ignore

export const initialState = {
	loginLoading: false, // 로그인 로딩
	loginDone: false,
	loginError: null,
	logoutLoading: false, //  로그아웃 로딩
	logoutDone: false,
	logoutError: null,
	signUpLoading: false, //  회원가입 로딩
	signUpDone: false,
	signUpError: null,
	changeNicknameLoading: false, // 닉네임 변경 시도중
	changeNicknameDone: false,
	changeNicknameError: null,
	me: null,
	signUpData: {},
	loginData: {},
};

const dummyUser = data => {
	return {
		...data,
		nickname: "KSR",
		id: 1,
		Posts: [{ id: 1 }],
		Followings: [{ nickname: "둘리" }, { nickname: "둘리" }, { nickname: "둘리" }],
		Followers: [{ nickname: "둘리" }, { nickname: "둘리" }, { nickname: "둘리" }],
	};
};
//  LOGIN
export const loginRequestAction = data => {
	return {
		type: LOG_IN_REQUEST,
		data,
	};
};
export const loginFailureAction = data => {
	return {
		type: LOG_IN_FAILURE,
		data,
	};
};

export const loginSuccessAction = data => {
	return {
		type: LOG_IN_SUCCESS,
		data,
	};
};

//	LOGOUT
export const logoutRequestAction = () => {
	return {
		type: LOG_OUT_REQUEST,
	};
};
export const logoutFailureAction = () => {
	return {
		type: LOG_OUT_FAILURE,
	};
};
export const logoutSuccessAction = () => {
	return {
		type: LOG_OUT_SUCCESS,
	};
};
//
const reducer = (state = initialState, action) => {
	switch (action.type) {
		case LOG_IN_REQUEST:
			return {
				...state,
				loginLoading: true,
			};
		case LOG_IN_SUCCESS:
			return {
				...state,
				loginLoading: false,
				loginDone: true,
				me: dummyUser(action.data),
			};
		case LOG_IN_FAILURE:
			return {
				...state,
				loginLoading: false,
				loginError: true,
			};
		case LOG_OUT_REQUEST:
			return {
				...state,
				logoutLoading: true,
				logoutError: null,
			};
		case LOG_OUT_SUCCESS:
			return {
				...state,
				logoutLoading: false,
				logoutDone: true,
				me: null,
			};
		case LOG_OUT_FAILURE:
			return {
				...state,
				logoutLoading: false,
				logoutError: action.error,
			};
		case SIGN_UP_REQUEST:
			return {
				...state,
				signUpLoading: true,
				signUpDone: null,
			};
		case SIGN_UP_SUCCESS:
			return {
				...state,
				signUpLoading: false,
				signUpDone: true,
			};
		case SIGN_UP_FAILURE:
			return {
				...state,
				signUpLoading: false,
				signUpError: action.error,
			};
		case CHANGE_NICKNAME_REQUEST:
			return {
				...state,
				changeNicknameLoading: true,
				changeNicknameDone: null,
			};
		case CHANGE_NICKNAME_SUCCESS:
			return {
				...state,
				changeNicknameLoading: false,
				changeNicknameDone: true,
			};
		case CHANGE_NICKNAME_FAILURE:
			return {
				...state,
				changeNicknameLoading: false,
				changeNicknameError: action.error,
			};
		case ADD_POST_TO_ME:
			return {
				...state,
				me: {
					...state.me,
					Posts: [{ id: action.data }, ...state.me.Posts],
				},
			};
		case REMOVE_POST_OF_ME:
			return {
				...state,
				me: {
					...state.me,
					Posts: state.me.Posts.filter(v => v.id !== action.data),
				},
			};
		default:
			return state;
	}
};
export default reducer;
